// bot.js - NEW streamlined entry point
const { createAppContainer } = require('./src/container');
const { TelegramBotAdapter } = require('./src/adapters/telegram/TelegramBotAdapter');

async function startBot(configPath) {
  const container = createAppContainer();

  // Load configuration
  const config = require(configPath);
  container.register('config', asValue(config));

  // Create bot adapter
  const botAdapter = container.resolve('telegramBotAdapter');

  // Setup event handlers
  botAdapter.setupEventHandlers();

  console.log('âœ… Bot started successfully');

  // Setup cleanup
