// tests/services/SessionService.test.js - Example test
describe('SessionService', () => {
  let sessionService;
  let mockStorage;
  let mockClaudeService;

  beforeEach(() => {
    mockStorage = { save: jest.fn(), find: jest.fn() };
    mockClaudeService = { createProcessor: jest.fn() };
    sessionService = new SessionService(mockStorage, mockClaudeService);
  });

  test('should create session with same logic as original', async () => {
    const userId = 'user123';
    const chatId = 'chat456';

    const session = await sessionService.createUserSession(userId, chatId);

    expect(session.userId).toBe(userId);
    expect(session.chatId).toBe(chatId);
    expect(mockStorage.save).toHaveBeenCalledWith(session);
  });
});
```

